function run(quantidade) {
  // Normaliza a quantidade a partir do menu (ex.: "2", "2 Cards", etc.)
  let qnt = 2;
  const q = (quantidade || '').toString();
  if (q.includes('3')) qnt = 3;
  if (q.includes('4')) qnt = 4;

  const items = [];
  for (let i = 1; i <= qnt; i++) {
    items.push({
      "header": {
        "type": "application/vnd.lime.media-link+json",
        "value": {
          "title": `Card ${i}`,
          "text": `Descri√ß√£o do card ${i}`,
          "type": "image/png",
          "uri": "https://s3-sa-east-1.amazonaws.com/msging.net/Services/Images/2e3ff2b4-a8c6-4188-bc0c-0f23531739b2"
        }
      },
      "options": [
        {
          "label": { "type": "text/plain", "value": "First option" },
          "value": { "type": "text/plain", "value": `first_option_${i}` }   // <- DOCUMENTO
        },
        {
          "label": { "type": "text/plain", "value": "Second option" },
          "value": { "type": "text/plain", "value": `second_option_${i}` }  // <- DOCUMENTO
        }
      ]
    });
  }

  return {
    "itemType": "application/vnd.lime.document-select+json",
    "items": items
  };
}

