function run(pedidosCliente) {
  try {
    if (typeof pedidosCliente !== "string") {
      pedidosCliente = JSON.stringify(pedidosCliente);
    }
    const data = JSON.parse(pedidosCliente);
    const cliente = Array.isArray(data) ? data[0] : data;
    if (!cliente || !cliente.itens) return "Sem itens para exibir.";

    let itensPedido = "";
    for (let i=0; i<cliente.itens.length; i++) {
      const item = cliente.itens[i];
      const ts = Number(item.dataPedido) * 1000;
      const dataFmt = new Date(ts).toLocaleString("pt-BR", {timeZone:"America/Sao_Paulo"});
      itensPedido += "<b>Produto:</b> " + item.nome + "\n" +
                     "Valor: R$" + item.valor + "\n" +
                     "M√©todo de pagamento: " + item.metodoPagamento + "\n" +
                     "Quantidade: " + item.quantidade + "\n" +
                     "Data do pedido: " + dataFmt + "\n\n";
    }

    const mensagemFinal = "<b>Pedidos feitos</b>\n" +
                          "Nome do cliente: " + cliente.nome + "\n\n" +
                          itensPedido;

    return mensagemFinal;

  } catch (e) {
    return "Erro no ExecuteScript: " + e.message;
  }
}

