            value: "finalizar"
        }

    ];

    return validateInputOptions(inputContent, regex_options, inputType, true);
}

function validateInputOptions(input, optionsRegexList, inputType, allowNumbers = false) {
    const invalid = "Input inesperado";

    if (isInvalidType(inputType)) {
        return invalid;
    }

    try {
        input = removeUnnecessaryInput(input);

        if (isInsideNumberOptionsInput(optionsRegexList, input) || allowNumbers) {
            for (let { regex, value } of optionsRegexList) {
                if (regex.test(input)) {
                    return value;
                }
            }

            // Se for n√∫mero (1-10), retorna correspondente
            const num = parseInt(input, 10);
            if (!isNaN(num) && num >= 1 && num <= optionsRegexList.length) {
                return optionsRegexList[num - 1].value;
            }
        }
    } catch (e) {
        return invalid;
    }

    return invalid;
}

function isInvalidType(inputType) {
    return inputType !== "text/plain";
}

function removeUnnecessaryInput(input) {
    const EMPTY_STR = "";
    const UNNECESSARY_INPUT = RegExp("(^\\.0+|^\\s+|\\s+$)", "gi");

    input = input.replace(/[*_]/g, "").trim();

    if (input.length > 1) {
        input = input.replace(UNNECESSARY_INPUT, EMPTY_STR);
    }

    return removeExcessWhiteSpace(input);
}

function removeExcessWhiteSpace(input) {
    return input.replace(/\s+/g, " ");
}

function isInsideNumberOptionsInput(optionsRegexList, input) {
    const matched = input.trim().match(/^\d+$/);
    if (!matched) return false;

    const number = parseInt(matched[0], 10);
    return number >= 1 && number <= optionsRegexList.length;
}
